// Enhanced input validation for Cursor + Claude + OpenRouter integration
const inputData = $input.all()[0].json;

// Validate required fields
if (!inputData.task_description) {
  throw new Error('task_description is required');
}

// Extract task context and determine routing strategy
const context = inputData.context || {};
const budgetConstraints = inputData.budget_constraints || { max_cost: 0.10 };
const cursorContext = inputData.cursor_context || {};
const claudeCrewContext = inputData.claude_crew_context || {};

// Determine if this should go to local Claude agents or OpenRouter
const shouldUseLocalClaude = context.use_local_claude || false;
const taskComplexity = context.task_complexity || 'medium';
const taskType = context.task_type || 'general';

// Prepare enhanced data structure for unified processing
return {
  // Core task information
  task_description: inputData.task_description,
  task_type: taskType,
  task_complexity: taskComplexity,

  // Context information
  context: context,
  cursor_context: cursorContext,
  claude_crew_context: claudeCrewContext,

  // Routing decisions
  routing_strategy: {
    use_local_claude: shouldUseLocalClaude,
    use_openrouter: !shouldUseLocalClaude,
    fallback_strategy: 'openrouter'
  },

  // Budget and constraints
  budget_constraints: budgetConstraints,

  // Metadata
  timestamp: new Date().toISOString(),
  request_id: $node["cursor-webhook-trigger"].json.request_id || Date.now().toString(),
  source: 'cursor_extension'
};

// Route to local Claude agents or OpenRouter based on task complexity and type
const routingData = $input.all()[0].json;
const { routing_strategy, task_complexity, task_type } = routingData;

// Decision logic for routing
let targetSystem = 'openrouter';
let reasoning = '';

if (routing_strategy.use_local_claude) {
  // Use local Claude agents for:
  // - Strategic planning (Captain Picard)
  // - Complex analysis (Commander Data)
  // - System architecture (Geordi La Forge)
  if (['strategic_planning', 'complex_analysis', 'system_architecture'].includes(task_type)) {
    targetSystem = 'local_claude';
    reasoning = `Task type '${task_type}' with ${task_complexity} complexity - routing to local Claude crew`;
  }
}

// Always use OpenRouter for:
// - Code generation (GPT-4o is excellent)
// - Quick analysis (Claude Haiku is cost-effective)
// - Multimodal tasks (GPT-4o supports images)
if (['code_generation', 'quick_analysis', 'multimodal'].includes(task_type)) {
  targetSystem = 'openrouter';
  reasoning = `Task type '${task_type}' - routing to OpenRouter for optimal performance`;
}

// Cost optimization: Use local Claude for high-complexity strategic tasks
if (task_complexity === 'high' && task_type === 'strategic_planning') {
  targetSystem = 'local_claude';
  reasoning = 'High-complexity strategic planning - using local Claude crew for cost optimization';
}

return {
  ...routingData,
  routing_decision: {
    target_system: targetSystem,
    reasoning: reasoning,
    estimated_cost: targetSystem === 'local_claude' ? 0.0 : 0.05,
    response_time: targetSystem === 'local_claude' ? 'fast' : 'medium'
  }
};

// Process the enhanced router output and format for Cursor UI
const routerOutput = $input.all()[0].json;

// Check if execution was successful
if (routerOutput.error) {
  return {
    success: false,
    error: routerOutput.error,
    timestamp: new Date().toISOString(),
    ui_feedback: {
      message: 'Routing failed - please try again',
      type: 'error',
      show_retry: true
    }
  };
}

// Parse the router output
const result = JSON.parse(routerOutput.stdout || '{}');

if (result.success) {
  // Enhanced response with UI elements for Cursor
  return {
    success: true,

    // Core response data
    ai_response: result.execution_result.response,
    model_used: result.execution_result.model_used,

    // Enhanced routing information
    routing_summary: {
      task_type: result.routing_summary.task_type,
      complexity: result.routing_summary.complexity,
      selected_model: result.routing_summary.selected_model,
      reasoning: result.routing_summary.reasoning,
      total_cost: result.routing_summary.total_cost,
      system_used: result.routing_summary.system_used
    },

    // UI enhancement data for Cursor
    ui_enhancements: {
      // Visual cues for LLM models
      model_visual_cue: {
        model_name: result.routing_summary.selected_model,
        provider: result.routing_summary.system_used,
        icon: result.routing_summary.system_used === 'local_claude' ? 'ü§ñ' : 'üåê',
        color: result.routing_summary.system_used === 'local_claude' ? '#00ff00' : '#0080ff'
      },

      // Cost display
      cost_display: {
        total_cost: result.routing_summary.total_cost,
        cost_breakdown: result.execution_result.cost_breakdown || {},
        cost_efficiency: result.routing_summary.cost_efficiency || 'high',
        savings_vs_alternative: result.routing_summary.savings_vs_alternative || 0.0
      },

      // Sub-agent consistency indicators
      sub_agent_status: {
        crew_member_used: result.routing_summary.crew_member || null,
        crew_consistency: result.routing_summary.crew_consistency || 'high',
        n8n_workflow_status: 'active',
        last_sync: new Date().toISOString()
      },

      // Performance metrics
      performance_metrics: {
        response_time: result.execution_result.response_time || 'fast',
        token_usage: result.execution_result.token_usage || {},
        model_confidence: result.routing_summary.confidence || 0.95
      }
    },

    // Alternative suggestions
    alternatives: result.llm_selection.alternatives || [],

    // Metadata
    timestamp: new Date().toISOString(),
    request_id: result.request_id
  };
} else {
  return {
    success: false,
    error: result.error || 'Unknown error occurred',
    timestamp: new Date().toISOString(),
    ui_feedback: {
      message: 'AI processing failed - check logs for details',
      type: 'error',
      show_retry: true
    }
  };
}

// Enhanced logging and monitoring for the unified system
const responseData = $input.all()[0].json;

if (responseData.success) {
  const { routing_summary, ui_enhancements } = responseData;

  console.log(`üöÄ Enhanced Unified AI Success:`);
  console.log(`   Task Type: ${routing_summary.task_type}`);
  console.log(`   Complexity: ${routing_summary.complexity}`);
