async function setupDatabase() {
  try {
    console.log('📋 Executing database schema...')

    // Split the SQL into individual statements
    const statements = schemaSQL
      .split(';')
      .map(stmt => stmt.trim())
      .filter(stmt => stmt.length > 0 && !stmt.startsWith('--'))

    console.log(`📝 Found ${statements.length} SQL statements to execute`)

    let successCount = 0
    let errorCount = 0

    for (let i = 0; i < statements.length; i++) {
      const statement = statements[i]
      if (statement.trim()) {
        try {
          console.log(`⏳ Executing statement ${i + 1}/${statements.length}...`)

          const { data, error } = await supabase.rpc('exec_sql', {
            sql: statement
          })

          if (error) {
            // Some errors are expected (like "already exists")
            if (error.message.includes('already exists') || 
                error.message.includes('does not exist') ||
                error.message.includes('duplicate key')) {
              console.log(`⚠️ Statement ${i + 1}: ${error.message}`)
            } else {
              console.error(`❌ Statement ${i + 1} failed:`, error.message)
              errorCount++
            }
          } else {
            console.log(`✅ Statement ${i + 1} executed successfully`)
            successCount++
          }
        } catch (err) {
          console.error(`❌ Statement ${i + 1} error:`, err.message)
          errorCount++
        }
      }
    }

    console.log(`\n📊 Database setup completed:`)
    console.log(`✅ Successful statements: ${successCount}`)
    console.log(`❌ Failed statements: ${errorCount}`)

    // Test the tables by trying to query them
    console.log('\n🧪 Testing database tables...')

    const tables = [
      'job_opportunities',
      'contacts', 
      'applications',
      'user_analytics_events',
      'user_sessions',
      'scraping_jobs',
      'scheduled_scraping_configs',
      'scheduled_scraping_status',
      'user_polling_preferences'
    ]

    for (const table of tables) {
      try {
        const { error } = await supabase
          .from(table)
          .select('*')
          .limit(1)

        if (error) {
          console.log(`❌ Table ${table}: ${error.message}`)
        } else {
          console.log(`✅ Table ${table}: Ready`)
        }
      } catch (err) {
        console.log(`❌ Table ${table}: ${err.message}`)
      }
    }

    console.log('\n🎉 Database setup process completed!')
    console.log('🚀 You can now test the API endpoints')

  } catch (error) {
    console.error('❌ Database setup failed:', error)
    process.exit(1)
  }
}

